mapper.update:
UPDATE RCP_CONT SET
RCPCT_CONTENT = #{RCPCT_CONTENT}
WHERE RCP_PK = ${RCP_PK}

UPDATE RCP_RESO SET
RCPRS_TITLE = #{RCPRS_TITLE},
RCPRS_AMOUNT = #{RCPRS_AMOUNT}
WHERE RCP_PK = ${RCP_PK}

UPDATE RCP SET
RCP_HIT = RCP_HIT + 1 
WHERE RCP_PK = #{RCP_PK}

mapper.select:
select RCPSP_PK
from RCP_SCRAP
WHERE RCP_PK=#{RCP_PK} and USR_ID=#{USR_ID}
